            y = args[j + 3];
            d.push("C", pf(args[j]), pf(args[j + 1]), pf(x), pf(y), pf(x), pf(y));
            j += 4;
            break;

          case _util.OPS.closePath:
            d.push("Z");
            break;
        }
      }

      d = d.join(" ");

      if (current.path &amp;&amp; ops.length &gt; 0 &amp;&amp; ops[0] !== _util.OPS.rectangle &amp;&amp; ops[0] !== _util.OPS.moveTo) {
