    }) =&gt; {
      if (pageNumber === previous) {
        return;
      }

      this._dispatchPageClose(previous);

      this._dispatchPageOpen(pageNumber);
    });

    this._internalEvents.set("pagerendered", ({
      pageNumber
    }) =&gt; {
      if (!this._pageOpenPending.has(pageNumber)) {
        return;
