        c2 = 22719;
  let r = key | 0,
      i,
      j;

  for (i = 0; i &lt; discardNumber; i++) {
    r = (data[i] + r) * c1 + c2 &amp; (1 &lt;&lt; 16) - 1;
  }

  const count = data.length - discardNumber;
  const decrypted = new Uint8Array(count);

  for (i = discardNumber, j = 0; j &lt; count; i++, j++) {
    const value = data[i];
    decrypted[j] = value ^ r &gt;&gt; 8;
    r = (value + r) * c1 + c2 &amp; (1 &lt;&lt; 16) - 1;
  }

  return decrypted;
}

function decryptAscii(data, key, discardNumber) {
  const c1 = 52845,
        c2 = 22719;
  let r = key | 0;
  const count = data.length,
        maybeLength = count &gt;&gt;&gt; 1;
  const decrypted = new Uint8Array(maybeLength);
  let i, j;

  for (i = 0, j = 0; i &lt; count; i++) {
    const digit1 = data[i];

    if (!isHexDigit(digit1)) {
      continue;
    }

    i++;
    let digit2;

    while (i &lt; count &amp;&amp; !isHexDigit(digit2 = data[i])) {
      i++;
    }

    if (i &lt; count) {
      const value = parseInt(String.fromCharCode(digit1, digit2), 16);
      decrypted[j++] = value ^ r &gt;&gt; 8;
      r = (value + r) * c1 + c2 &amp; (1 &lt;&lt; 16) - 1;
    }
  }

  return decrypted.slice(discardNumber, j);
}

function isSpecial(c) {
  return c === 0x2f || c === 0x5b || c === 0x5d || c === 0x7b || c === 0x7d || c === 0x28 || c === 0x29;
}

class Type1Parser {
  constructor(stream, encrypted, seacAnalysisEnabled) {
    if (encrypted) {
      const data = stream.getBytes();
      const isBinary = !((isHexDigit(data[0]) || (0, _core_utils.isWhiteSpace)(data[0])) &amp;&amp; isHexDigit(data[1]) &amp;&amp; isHexDigit(data[2]) &amp;&amp; isHexDigit(data[3]) &amp;&amp; isHexDigit(data[4]) &amp;&amp; isHexDigit(data[5]) &amp;&amp; isHexDigit(data[6]) &amp;&amp; isHexDigit(data[7]));
      stream = new _stream.Stream(isBinary ? decrypt(data, EEXEC_ENCRYPT_KEY, 4) : decryptAscii(data, EEXEC_ENCRYPT_KEY, 4));
    }

    this.seacAnalysisEnabled = !!seacAnalysisEnabled;
    this.stream = stream;
    this.nextChar();
  }

  readNumberArray() {
    this.getToken();
    const array = [];

    while (true) {
      const token = this.getToken();

      if (token === null || token === "]" || token === "}") {
        break;
      }

      array.push(parseFloat(token || 0));
    }

    return array;
  }

  readNumber() {
    const token = this.getToken();
    return parseFloat(token || 0);
  }

  readInt() {
    const token = this.getToken();
    return parseInt(token || 0, 10) | 0;
  }

  readBoolean() {
    const token = this.getToken();
    return token === "true" ? 1 : 0;
  }

  nextChar() {
    return this.currentChar = this.stream.getByte();
  }

  prevChar() {
    this.stream.skip(-2);
    return this.currentChar = this.stream.getByte();
  }

  getToken() {
    let comment = false;
    let ch = this.currentChar;

    while (true) {
      if (ch === -1) {
        return null;
      }

      if (comment) {
        if (ch === 0x0a || ch === 0x0d) {
          comment = false;
        }
      } else if (ch === 0x25) {
        comment = true;
      } else if (!(0, _core_utils.isWhiteSpace)(ch)) {
        break;
      }

      ch = this.nextChar();
    }

    if (isSpecial(ch)) {
      this.nextChar();
      return String.fromCharCode(ch);
    }

    let token = "";

    do {
      token += String.fromCharCode(ch);
      ch = this.nextChar();
    } while (ch &gt;= 0 &amp;&amp; !(0, _core_utils.isWhiteSpace)(ch) &amp;&amp; !isSpecial(ch));

    return token;
  }

  readCharStrings(bytes, lenIV) {
    if (lenIV === -1) {
      return bytes;
    }

    return decrypt(bytes, CHAR_STRS_ENCRYPT_KEY, lenIV);
  }

  extractFontProgram(properties) {
    const stream = this.stream;
    const subrs = [],
          charstrings = [];
    const privateData = Object.create(null);
    privateData.lenIV = 4;
    const program = {
      subrs: [],
      charstrings: [],
      properties: {
        privateData
      }
    };
    let token, length, data, lenIV;

    while ((token = this.getToken()) !== null) {
      if (token !== "/") {
        continue;
      }

      token = this.getToken();

      switch (token) {
        case "CharStrings":
          this.getToken();
          this.getToken();
          this.getToken();
          this.getToken();

          while (true) {
            token = this.getToken();

            if (token === null || token === "end") {
              break;
            }

            if (token !== "/") {
              continue;
            }

            const glyph = this.getToken();
            length = this.readInt();
            this.getToken();
            data = length &gt; 0 ? stream.getBytes(length) : new Uint8Array(0);
            lenIV = program.properties.privateData.lenIV;
            const encoded = this.readCharStrings(data, lenIV);
            this.nextChar();
            token = this.getToken();

            if (token === "noaccess") {
              this.getToken();
            } else if (token === "/") {
              this.prevChar();
            }

            charstrings.push({
              glyph,
              encoded
            });
          }

          break;

        case "Subrs":
          this.readInt();
          this.getToken();

          while (this.getToken() === "dup") {
            const index = this.readInt();
            length = this.readInt();
            this.getToken();
            data = length &gt; 0 ? stream.getBytes(length) : new Uint8Array(0);
            lenIV = program.properties.privateData.lenIV;
            const encoded = this.readCharStrings(data, lenIV);
            this.nextChar();
            token = this.getToken();

            if (token === "noaccess") {
              this.getToken();
            }

            subrs[index] = encoded;
          }

          break;

        case "BlueValues":
        case "OtherBlues":
        case "FamilyBlues":
        case "FamilyOtherBlues":
          const blueArray = this.readNumberArray();

          if (blueArray.length &gt; 0 &amp;&amp; blueArray.length % 2 === 0 &amp;&amp; HINTING_ENABLED) {
            program.properties.privateData[token] = blueArray;
          }

          break;

        case "StemSnapH":
        case "StemSnapV":
          program.properties.privateData[token] = this.readNumberArray();
          break;

        case "StdHW":
        case "StdVW":
          program.properties.privateData[token] = this.readNumberArray()[0];
          break;

        case "BlueShift":
        case "lenIV":
        case "BlueFuzz":
        case "BlueScale":
        case "LanguageGroup":
        case "ExpansionFactor":
          program.properties.privateData[token] = this.readNumber();
          break;

        case "ForceBold":
          program.properties.privateData[token] = this.readBoolean();
          break;
      }
    }

    for (const {
      encoded,
      glyph
    } of charstrings) {
      const charString = new Type1CharString();
      const error = charString.convert(encoded, subrs, this.seacAnalysisEnabled);
      let output = charString.output;

      if (error) {
        output = [14];
      }

      const charStringObject = {
        glyphName: glyph,
        charstring: output,
        width: charString.width,
        lsb: charString.lsb,
        seac: charString.seac
      };

      if (glyph === ".notdef") {
        program.charstrings.unshift(charStringObject);
      } else {
        program.charstrings.push(charStringObject);
      }

      if (properties.builtInEncoding) {
        const index = properties.builtInEncoding.indexOf(glyph);

        if (index &gt; -1 &amp;&amp; properties.widths[index] === undefined &amp;&amp; index &gt;= properties.firstChar &amp;&amp; index &lt;= properties.lastChar) {
          properties.widths[index] = charString.width;
        }
      }
    }

    return program;
  }

  extractFontHeader(properties) {
    let token;

    while ((token = this.getToken()) !== null) {
      if (token !== "/") {
        continue;
      }

      token = this.getToken();

      switch (token) {
        case "FontMatrix":
          const matrix = this.readNumberArray();
          properties.fontMatrix = matrix;
          break;

        case "Encoding":
          const encodingArg = this.getToken();
          let encoding;

          if (!/^\d+$/.test(encodingArg)) {
            encoding = (0, _encodings.getEncoding)(encodingArg);
          } else {
            encoding = [];
            const size = parseInt(encodingArg, 10) | 0;
            this.getToken();

            for (let j = 0; j &lt; size; j++) {
              token = this.getToken();

              while (token !== "dup" &amp;&amp; token !== "def") {
                token = this.getToken();

                if (token === null) {
                  return;
                }
              }

              if (token === "def") {
                break;
              }

              const index = this.readInt();
              this.getToken();
              const glyph = this.getToken();
              encoding[index] = glyph;
              this.getToken();
            }
          }

          properties.builtInEncoding = encoding;
          break;

        case "FontBBox":
          const fontBBox = this.readNumberArray();
          properties.ascent = Math.max(fontBBox[3], fontBBox[1]);
          properties.descent = Math.min(fontBBox[1], fontBBox[3]);
          properties.ascentScaled = true;
          break;
      }
    }
  }

}

